import { SUPABASE_URL, SUPABASE_PUBLISHABLE_KEY } from './credentials.js';
import { createClient } from 'https://cdn.jsdelivr.net/npm/@supabase/supabase-js@2/+esm';

// Create and export Supabase client
export const supabase = createClient(SUPABASE_URL, SUPABASE_PUBLISHABLE_KEY);

// Verify configuration on load
if (SUPABASE_URL === 'YOUR_SUPABASE_URL' || SUPABASE_PUBLISHABLE_KEY === 'YOUR_SUPABASE_PUBLISHABLE_KEY') {
  console.warn('⚠️ Supabase credentials not configured! Please update credentials.js with your actual credentials.');
}
